import React, { useMemo, useState } from "react";

import { FaStar } from "react-icons/fa";
import { NavLink } from "react-router-dom";
import { useGlobalContext } from "./context";
import Pagination from "./Pagination";

const Movies = () => {
     let PageSize = 6;
     const { movie, setMovie } = useGlobalContext();
     const [currentPage, setCurrentPage] = useState(1);

     const currentTableData = useMemo(() => {
          const firstPageIndex = (currentPage - 1) * PageSize;
          const lastPageIndex = firstPageIndex + PageSize;
          return movie.slice(firstPageIndex, lastPageIndex);
     }, [currentPage]);
     return (
          <>
               <section className='movie-page'>
                    <h3 className='tranding_title'>Trending</h3>
                    <div className='container grid grid-4-col'>
                         {movie.map((curMovie) => {
                              var { id, title, poster_path } = curMovie;

                              const movieName = title.substring(0, 15);
                              return (
                                   <NavLink to={`movie/${id}`} key={id}>
                                        <div className='card'>
                                             <div className='card-info'>
                                                  <img
                                                       src={`https://image.tmdb.org/t/p/w500//${poster_path}`}
                                                       alt=''
                                                  />
                                                  <div className='card_title'>
                                                       <div>
                                                            <h5>
                                                                 {movieName.length >=
                                                                 15
                                                                      ? `${movieName} ...`
                                                                      : movieName}
                                                            </h5>
                                                            <span className='rating'>
                                                                 <FaStar className='star' />
                                                                 4.6/5
                                                            </span>
                                                       </div>
                                                  </div>
                                             </div>
                                        </div>
                                   </NavLink>
                              );
                         })}
                    </div>

                    <Pagination
                         className='pagination-bar'
                         currentPage={currentPage}
                         totalCount={movie.length}
                         pageSize={PageSize}
                         onPageChange={(page) => setCurrentPage(page)}
                    />
               </section>
          </>
     );
};

export default Movies;
