import React, { useContext, useEffect, useState } from "react";
const API_URl = `https://api.themoviedb.org/3/movie/popular?api_key=${process.env.REACT_APP_API_KEY}&language=en-US&page=1`;
const API_SEARCH = `https://api.themoviedb.org/3/search/movie?api_key=${process.env.REACT_APP_API_KEY}`;
const AppContext = React.createContext();
// we need to create a provider fun
const AppProvider = ({ children }) => {
     const [isLoading, setIsLoading] = useState(true);
     const [movie, setMovie] = useState([]);
     const [isError, setIsError] = useState({ show: "false", msg: "" });

     const getMovies = async (url) => {
          try {
               const res = await fetch(url);
               const data = await res.json();
               console.log(data.results);
               if (data.results.length > 0) {
                    setIsLoading(false);
                    setMovie(data.results);
               } else {
                    setIsError({
                         show: true,
                         msg: data.error,
                    });
               }
          } catch (error) {
               console.log(error);
          }
     };
     useEffect(() => {
          getMovies(API_URl);
     }, []);

     const onSearch = (query) => {
          let timerOut = setTimeout(() => {
               getMovies(`${API_SEARCH}&query=${query}`);
          }, 3000);
          return () => clearTimeout(timerOut);
     };
     //  const searchMovie = async (e) => {
     //       e.preventDefault();
     //       console.log("Searching");
     //       try {
     //            const url = `https://api.themoviedb.org/3/search/movie?api_key=${process.env.REACT_APP_API_KEY}`;
     //            const res = await fetch(url);
     //            const data = await res.json();
     //            console.log(data);
     //            setMovie(data.results);
     //       } catch (e) {
     //            console.log(e);
     //       }
     //  };
     //  searchMovie(API_SEARCH);

     //  const changeHandler = (e) => {
     //       setQuery(e.target.value);
     //  };

     return (
          <AppContext.Provider value={{ isLoading, isError, movie, onSearch }}>
               {children}
          </AppContext.Provider>
     );
};

//Global custom hooks
const useGlobalContext = () => {
     return useContext(AppContext);
};

export { AppContext, AppProvider, useGlobalContext };
